export const getDelay=t=>{const e=t.getAttribute("data-delay");return e?+e:0};export const getDuration=t=>{const e=t.getAttribute("data-duration");return e?+e:1};export const getEffect=t=>t.getAttribute("data-effect");export const random=(t,e)=>Math.random()*(e-t)+t;export const setDelay=(t,e)=>{t.setAttribute("data-delay",+e.toFixed(2))};