import{addClass,removeClass}from"./handleClassnames.js";export const showPopup=s=>{const o=document.querySelector(".popups"),e=o.querySelector(s);o.style.transitionDelay="0s",addClass(o,"show"),addClass(e,"show")};export const closePopup=()=>{const s=document.querySelector(".popups");s.querySelectorAll(".popup").forEach((s=>{removeClass(s,"show")})),s.style.transitionDelay="0.2s",removeClass(s,"show")};