"use strict";const startDemo=()=>{const e=document.querySelector(".demo"),t=document.querySelector(".hero");e.style.display="none",t.classList.add("show")};document.querySelector("#start-demo").addEventListener("click",(function(){setInterval(startDemo,5e3)}));const paginationChange=()=>{const e=document.querySelectorAll(".preview-item");e.forEach((e=>{e.classList.remove("active")})),e[swiper.activeIndex].classList.add("active")},swiper=new Swiper(".demo-slider",{navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},pagination:{el:".swiper-pagination",clickable:!0}});paginationChange(),swiper.on("slideChange",(function(e){paginationChange()}));const previewsElements=document.querySelectorAll(".preview-item");previewsElements.forEach(((e,t)=>{e.addEventListener("click",(e=>{swiper.slideTo(t)}))}));