const powerEl=document.querySelector("#power"),energyEl=document.querySelector("#energy"),luckEl=document.querySelector("#luck"),scoreEl=document.querySelector("#score"),summEl=document.querySelector("#summ");export class CalcStats{constructor(t,e,s,o,r,c){this.stats={power:t,energy:e,luck:s},this.staticScore=r,this.score=o,this.summ=c}render(){powerEl.textContent=this.stats.power.toFixed(1),energyEl.textContent=this.stats.energy.toFixed(1),luckEl.textContent=this.stats.luck.toFixed(1),scoreEl.textContent=0==this.score.toFixed(1)?0:`+${this.score.toFixed(1)}`,summEl.textContent=`$${this.summ.toFixed(0)}`}setStat(){return{stats:this.stats,score:this.score,summ:this.summ}}statPlus(t){this.score.toFixed(1)>0&&(this.stats[t]+=.1,this.score-=.1,this.summ+=.1,this.render())}statMinus(t){this.score.toFixed(1)<this.staticScore&&(this.stats[t]-=.1,this.score+=.1,this.summ-=.1,this.render())}}