import{toogleClass}from"./toogleClass.js";export const burger=document.querySelector(".burger");const headerMenues=document.querySelectorAll(".menu"),mobMenu=document.querySelector(".mobile-menu"),mobMenuContainer=document.querySelector(".mobile-menu__container"),header=document.querySelector(".header"),headerLeft=document.querySelector(".header__left"),headerRight=document.querySelector(".header__right");export const setMenu=()=>{const e=window.innerWidth;if(e<=992)for(let e of headerMenues)mobMenuContainer.append(e);else if(e>992)for(let e of headerMenues)e.classList.contains("header__left-menu")?headerLeft.append(e):headerRight.prepend(e)};export const toogleMenu=()=>{toogleClass(burger,"active"),toogleClass(header,"header_mobile"),toogleClass(mobMenu,"open")};